package newprogram;

public class forwhiledowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++) {
			System.out.println(i);
		}
		for (int i= 15; i >= 1; i--) {
			System.out.println(i);
		}
		int m = 1;
		while(m<=10) {
			System.out.println(m);
			m++;
		}
		
		int n =9;
		do {
			System.out.println(n);
			n++;
		} while (n <= 10);

	}

}
