package newprogram;

public class posnegzero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 12;
		if (num > 0) {
			System.out.println("Positive");
		}
		else if (num  < 0) {
			System.out.println("Negative");
		}
		else if (num == 0) {
			System.out.println("Zero");
		}

	}

}
